import React from 'react';
import photoProfile from '../images/photo-profile.jpg';
import editButton from '../images/Edit__Button.svg';
import pluse from '../images/pluse.svg';

function Main(props) {

  function handleEditAvatarClick() {
   console.log('click edit avatar');
   document.querySelector('.popup_type_update-avatar').classList.add('popup_opened')
  };

  function handleEditProfileClick() {
    console.log('click edit profile');
    document.querySelector('.popup_type_edit-profile').classList.add('popup_opened')
  };

  function handleAddPlaceClick() {
    console.log('click add place');
    document.querySelector('.popup_type_add-card').classList.add('popup_opened')
  };

    return (
        <main className="content">
          <section className="profile">
            <div className="profile__button-avatar" onClick={handleEditAvatarClick}>
              <img 
              className="profile__photo"
              src={photoProfile}
              alt="Фотография владельца профиля"
              />
            </div>
            <div className="profile__info">
              <h1 className="profile__name">Жак-Ив Кусто</h1>
              <button 
              className="profile__button"
              type="button"
              onClick={handleEditProfileClick}
              >
                <img
              src={editButton}
                alt="Кнопка 'Редактировать профиль'"
                className="profile__edit-button"
                />
              </button>
              <p className="profile__interests">Исследователь океана</p>  
            </div>
            <button
            className="profile__add-photo"
            type="button"
            onClick={handleAddPlaceClick}
            >
              <img
              src={pluse}
              alt="Кнопка 'Добавить фото в профиль'"
              className="profile__button-pluse"
              />
            </button>
          </section>
          <section className="cards">
            {/*сюда склонируется темплейт элемент*/}
          </section>

        </main>
    )
}


export default Main;
